    private void startButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_startButtonActionPerformed
       
        MyThread t1 = null;
        MyThread t2 = null;
        
        t1 = new MyThread(1, (id, value) -> {
            if (value % 1_000 == 0){
                labelCounter1.setText(String.valueOf(value));
            }
           
       });
        
        t2 = new MyThread(2, (id, value) -> {
            if (value % 1_000 == 0){
                labelCounter2.setText(String.valueOf(value));
            }
           
       });
        
        t1.start(10_000_000, 50_000_000);
        t2.start(20_000_000, 40_000_000);
    }//GEN-LAST:event_startButtonActionPerformed

